import React from 'react';
import { toast } from 'react-toastify';

const DoctorRow = ({doctor,index,refetch,setDeletingDoctor}) => {
    const {img,name,specialty,email} = doctor;
    const handleDeletingDoctor = (email)=>{
        const url =`http://localhost:5000/doctors/${email}`;
        fetch(url,{
            method:'DELETE',
            headers:{
                'authorization': `user ${localStorage.getItem('accessToken')}`
            }
        })
        .then(res => res.json())
        .then(data => {
            if(data.deletedCount){
                toast.success()
            }
        })
    }
    return (
        <tr>
            <th>{index + 1}</th>
            <td>
                <div className="avatar">
                    <div className="w-8 rounded">
                        <img src={img} alt={name} />
                    </div>
                </div>
            </td>
            <td>{name}</td>
            <td>{specialty}</td>
            <td>
                <label onClick={() => handleDeletingDoctor(email)} htmlFor="delete-confirm-modal" className="btn btn-xs btn-error">Delete</label>
            </td>
        </tr>
    );
};

export default DoctorRow;
