import React from 'react';
import { toast } from 'react-toastify';
const UserRow = ({user,refetch,index}) => {
    const {role,email} = user;
    const makeAdmin = () =>{
        fetch(`http://localhost:5000/users/admin/${email}`,{
            method:'PUT',
            headers:{
                'authorization':`user ${localStorage.getItem('accessToken')}`
            },
        })
        .then(res =>{
            if(role === 'admin'){
                toast.success('Successfully made admin');
                refetch();
            }else{
                toast.warning("Sorry , you can't make a person admin");
            }
        })
    }
    return (
        <tr>
            <th>{index+1}</th>
            <td>{email}</td>
            <td>
                {
                    role !== 'admin' ? <button onClick={makeAdmin} className="rounded-xl btn-xs">Make Admin</button> : 'This person is an admin' 
                }
            </td>
            <td><button className="rounded-xl btn-xs">Remove User</button></td>
        </tr>
    );
};

export default UserRow;
