import axios from 'axios';
import React from 'react';
const UserRow = ({user,refetch,index}) => {
    const {name,email} = user;
    const makeAdmin =()=>{
        axios.put(`http://localhost:5000/users/admin/${email}`,{
            headers:{
                'authorization':`user ${localStorage.getItem('accessToken')}`
            },
        })
        .then(res =>console.log(res))
        .catch(err=>console.log(err.code))
    }
    return (
        <tr>
            <th>{index}</th>
            <td>{email}</td>
            <td><button onClick={makeAdmin} className="rounded-xl btn-xs">Make Admin</button></td>
            <td><button className="rounded-xl btn-xs">Remove User</button></td>
        </tr>
    );
};

export default UserRow;
